mapper.insert:
insert into screen (id, username, password, 
  room_id, user_id, duration, 
  times, type, resolution
  )
values (#{id,jdbcType=VARCHAR}, #{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{roomId,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, #{duration,jdbcType=VARCHAR}, 
  #{times,jdbcType=INTEGER}, #{type,jdbcType=VARCHAR}, #{resolution,jdbcType=VARCHAR}
  )

mapper.select:
select 
	s.id s_id, s.username s_username, s.password, s.room_id, s.user_id, s.duration, s.times, s.type, s.resolution,
	r.id r_id, r.num, r.position, r.`desc`,
	u.id u_id, u.username u_username
from screen s left join room r on s.room_id=r.id
left join user u on s.user_id=u.id


		and s.id=#{id}


		and s.username=#{username}


		and s.user_id=#{userId}


		and concat (s.username,r.num,s.id,u.username) like concat ('%',#{duration},'%')
