mapper.select:
	select 
		id, username, password, truename, sex, telephone, email, job, job_type, role, admin_id, screen_num, 
  	duration, times, remake
  from user


  		and id=#{id}


  		and username=#{username}


  		and telephone=#{telephone}


  		and admin_id=#{adminId}


  		and role=#{role}


  		and concat (username,truename) like concat ('%',#{truename},'%')
